insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (108,1923,'Entrada de mercadoria recebida do vendedor remetente, em venda à ordem','Classificam-se neste código as entradas de mercadorias recebidas do vendedor remetente, em vendas à ordem, cuja compra do adquirente originário, foi classificada nos códigos 1.120 - Compra para industrialização, em venda à ordem, já recebida do vendedor remetente ou 1.121 - Compra para comercialização, em venda à ordem, já recebida do vendedor remetente.');
