insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (118,2101,'Compra para industrialização','Classificam-se neste código as compras de mercadorias a serem utilizadas em processo de industrialização. Também serão classificadas neste código as entradas de mercadorias em estabelecimento industrial de cooperativa recebidas de seus cooperados ou de estabelecimento de outra cooperativa.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (119,2102,'Compra para comercialização','Classificam-se neste código as compras de mercadorias a serem comercializadas. Também serão classificadas neste código as entradas de mercadorias em estabelecimento comercial de cooperativa recebidas de seus cooperados ou de estabelecimento de outra cooperativa.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (120,2111,'Compra para industrialização de mercadoria recebida anteriormente em consignação industrial','Classificam-se neste código as compras efetivas de mercadorias a serem utilizadas em processo de industrialização, recebidas anteriormente a título de consignação industrial.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (121,2113,'Compra para comercialização, de mercadoria recebida anteriormente em consignação mercantil','Classificam-se neste código as compras efetivas de mercadorias recebidas anteriormente a título de consignação mercantil.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (122,2116,'Compra para industrialização originada de encomenda para recebimento futuro','Classificam-se neste código as compras de mercadorias a serem utilizadas em processo de industrialização, quando da entrada real da mercadoria, cuja aquisição tenha sido classificada no código 2.922 - Lançamento efetuado a título de simples faturamento decorrente de compra para recebimento futuro.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (123,2117,'Compra para comercialização originada de encomenda para recebimento futuro','Classificam-se neste código as compras de mercadorias a serem comercializadas, quando da entrada real da mercadoria, cuja aquisição tenha sido classificada no código 2.922 - Lançamento efetuado a título de simples faturamento decorrente de compra para recebimento futuro.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (124,2118,'Compra de mercadoria para comercialização pelo adquirente originário, entregue pelo vendedor remetente ao destinatário, em venda à ordem','Classificam-se neste código as compras de mercadorias já comercializadas, que, sem transitar pelo estabelecimento do adquirente originário, sejam entregues pelo vendedor remetente diretamente ao destinatário, em operação de venda à ordem, cuja venda seja classificada, pelo adquirente originário, no código 6.120 - Venda de mercadoria adquirida ou recebida de terceiros entregue ao destinatário pelo vendedor remetente, em venda à ordem.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (125,2120,'Compra para industrialização, em venda à ordem, já recebida do vendedor remetente','Classificam-se neste código as compras de mercadorias a serem utilizadas em processo de industrialização, em vendas à ordem, já recebidas do vendedor remetente, por ordem do adquirente originário.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (126,2121,'Compra para comercialização, em venda à ordem, já recebida do vendedor remetente','Classificam-se neste código as compras de mercadorias a serem comercializadas, em vendas à ordem, já recebidas do vendedor remetente por ordem do adquirente originário.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (127,2122,'Compra para industrialização em que a mercadoria foi remetida pelo fornecedor ao industrializador sem transitar pelo estabelecimento adquirente','Classificam-se neste código as compras de mercadorias a serem utilizadas em processo de industrialização, remetidas pelo fornecedor para o industrializador sem que a mercadoria tenha transitado pelo estabelecimento do adquirente.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (128,2124,'Industrialização efetuada por outra empresa','Classificam-se neste código as entradas de mercadorias industrializadas por terceiros, compreendendo os valores referentes aos serviços prestados e os das mercadorias de propriedade do industrializador empregadas no processo industrial. Quando a industrialização efetuada se referir a bens do ativo imobilizado ou de mercadorias para uso ou consumo do estabelecimento encomendante, a entrada deverá ser classificada nos códigos 2.551 - Compra de bem para o ativo imobilizado ou 2.556 - Compra de material para uso ou consumo.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (129,2125,'Industrialização efetuada por outra empresa quando a mercadoria remetida para utilização no processo de industrialização não transitou pelo estabelecimento adquirente da mercadoria','Classificam-se neste código as entradas de mercadorias industrializadas por outras empresas, em que as mercadorias remetidas para utilização no processo de industrialização não transitaram pelo estabelecimento do adquirente das mercadorias, compreendendo os valores referentes aos serviços prestados e os das mercadorias de propriedade do industrializador empregadas no processo industrial. Quando a industrialização efetuada se referir a bens do ativo imobilizado ou de mercadorias para uso ou consumo do estabelecimento encomendante, a entrada deverá ser classificada nos códigos 2.551 - Compra de bem para o ativo imobilizado ou 2.556 - Compra de material para uso ou consumo.');
