insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (216,2923,'Entrada de mercadoria recebida do vendedor remetente, em venda à ordem','Classificam-se neste código as entradas de mercadorias recebidas do vendedor remetente, em vendas à ordem, cuja compra do adquirente originário, foi classificada nos códigos 2.120 - Compra para industrialização, em venda à ordem, já recebida do vendedor remetente ou 2.121 - Compra para comercialização, em venda à ordem, já recebida do vendedor remetente.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (217,2924,'Entrada para industrialização por conta e ordem do adquirente da mercadoria, quando esta não transitar pelo estabelecimento do adquirente','Classificam-se neste código as entradas de insumos recebidos para serem industrializados por conta e ordem do adquirente, nas hipóteses em que os insumos não tenham transitado pelo estabelecimento do adquirente dos mesmos.');
