insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (258,5101,'Venda de produção do estabelecimento','Classificam-se neste código as vendas de produtos industrializados no estabelecimento. Também serão classificadas neste código as vendas de mercadorias por estabelecimento industrial de cooperativa destinadas a seus cooperados ou a estabelecimento de outra cooperativa.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (259,5102,'Venda de mercadoria adquirida ou recebida de terceiros','Classificam-se neste código as vendas de mercadorias adquiridas ou recebidas de terceiros para industrialização ou comercialização, que não tenham sido objeto de qualquer processo industrial no estabelecimento. Também serão classificadas neste código as vendas de mercadorias por estabelecimento comercial de cooperativa destinadas a seus cooperados ou estabelecimento de outra cooperativa.');
