insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (261,5104,'Venda de mercadoria adquirida ou recebida de terceiros, efetuada fora do estabelecimento','Classificam-se neste código as vendas efetuadas fora do estabelecimento, inclusive por meio de veículo, de mercadorias adquiridas ou recebidas de terceiros para industrialização ou comercialização, que não tenham sido objeto de qualquer processo industrial no estabelecimento.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (262,5105,'Venda de produção do estabelecimento que não deva por ele transitar','Classificam-se neste código as vendas de produtos industrializados no estabelecimento, armazenados em depósito fechado, armazém geral ou outro sem que haja retorno ao estabelecimento depositante.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (263,5106,'Venda de mercadoria adquirida ou recebida de terceiros, que não deva por ele transitar','Classificam-se neste código as vendas de mercadorias adquiridas ou recebidas de terceiros para industrialização ou comercialização, armazenadas em depósito fechado, armazém geral ou outro, que não tenham sido objeto de qualquer processo industrial no estabelecimento sem que haja retorno ao estabelecimento depositante. Também serão classificadas neste código as vendas de mercadorias importadas, cuja saída ocorra do recinto alfandegado ou da repartição alfandegária onde se processou o desembaraço aduaneiro, com destino ao estabelecimento do comprador, sem transitar pelo estabelecimento do importador.');
insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (264,5109,'Venda de produção do estabelecimento, destinada à Zona Franca de Manaus ou Áreas de Livre Comércio','Classificam-se neste código as vendas de produtos industrializados pelo estabelecimento, destinados à Zona Franca de Manaus ou Áreas de Livre Comércio.');
