insert  into `cfop`(`ID`,`CFOP`,`DESCRICAO`,`APLICACAO`) values (266,5111,'Venda de produção do estabelecimento remetida anteriormente em consignação industrial','Classificam-se neste código as vendas efetivas de produtos industrializados no estabelecimento remetidos anteriormente a título de consignação industrial.');
